import React, {useState} from 'react'
export default Login=()=>{
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    // Simple validation
    if (!username.includes('@') || !username.includes('.') || password.length < 8) {
      setError('Invalid username or password.');
    } else {
      // Mocking authentication
      // Replace this with actual authentication logic (e.g., calling an API)
      if (username === 'example@email.com' && password === 'password123') {
        // Redirect to home page on successful login
        console.log('success')
        // window.location.href = '/home'; // You can use react-router for better navigation
      } else {
        setError('Invalid username or password.');
      }
    }
    return(
       <>
       <div>
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
            <div>
               <label htmlFor="username">Username/Email</label>
                <input>
                type="email"
                id="username"
                value={username}
                onChange={(e)=>setUsername(e.target.value)}
                </input>
            </div>
            <div>
               <label htmlFor="password">Password:</label>
                <input>
                type="password"
                id="password"
                value={password}
                onChange={(e)=>setPassword(e.target.value)}
                </input>
            </div>
            <button type="submit">Login</button>
        </form>

       </div>

       </>
    );

}
}
