import React, { useState } from 'react';
import { lazy } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './Login';


import Navbar from './Navbar';



const Home = React.lazy(async () => await import('mfehome/HomeApp'));
const User = React.lazy(async () => await import('mfeuser/UserApp'));
// const Home= lazy(() => import('mfehome/HomeApp'));
// const User= lazy(() => import('mfeuser/UserApp'));
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {

    setIsLoggedIn(true);
  };

  const handleLogout = () => {

    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div className="App">
        {isLoggedIn && <Navbar onLogout={handleLogout} />}
        <Routes>
          <Route
            path="/"
            element={<Login onLogin={handleLogin} />}
          />
          {isLoggedIn && (
            <>
              <Route path="/home" element={<React.Suspense fallback ="Loading"><Home/></React.Suspense>}/>
              <Route path="/usermanagement" element={<React.Suspense fallback ="Loading"><User/></React.Suspense>}/>
              {/* <Route path="/usermanagement" element={<User/>} /> */}
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
