import React, { useState } from 'react';
import './App.css';
import { AiFillEdit } from "react-icons/ai";
import { AiFillDelete } from "react-icons/ai";



const UserManagementPage = () => {
  const [users, setUsers] = useState([
    { id: 1, firstName: 'John', lastName: 'Doe', email: 'john.doe@example.com' },
    { id: 2, firstName: 'Jane', lastName: 'Doe', email: 'jane.doe@example.com' },
  ]);

  const [editingUserId, setEditingUserId] = useState(null);
  const [newUser, setNewUser] = useState({ firstName: '', lastName: '', email: '' });
  const [showAddUserForm, setShowAddUserForm] = useState(false);

  const handleEdit = (userId) => {
    setEditingUserId(userId);
  };

  const handleSave = (userId) => {
    setEditingUserId(null);
    // Add logic to save changes (e.g., send data to a backend)
  };

  const handleCancelEdit = () => {
    setEditingUserId(null);
  };

  const handleDelete = (userId) => {
    setUsers(users.filter(user => user.id !== userId));
  };

  const handleInputChange = (userId, field, value) => {
    setUsers(prevUsers =>
      prevUsers.map(user =>
        user.id === userId ? { ...user, [field]: value } : user
      )
    );
  };

//   const handleNewUserInputChange = (field, value) => {
//     setNewUser(prevNewUser => ({ ...prevNewUser, [field]: value }));
//   };

  const handleAddUser = () => {
    setShowAddUserForm(true);
  };

  const handleSaveNewUser = () => {
    setUsers(prevUsers => [...prevUsers, { id: users.length + 1, ...newUser }]);
    setNewUser({ firstName: '', lastName: '', email: '' });
    setShowAddUserForm(false);
  };

  const handleCancelNewUser = () => {
    setNewUser({ firstName: '', lastName: '', email: '' });
    setShowAddUserForm(false);
  };

  return (
    <div>
      <h1>User Management</h1>
      <button className='add' onClick={handleAddUser}>Add User</button>
      {showAddUserForm && (
        <div>
          <label>First Name: </label>
          <input
            type="text"
            value={newUser.firstName}
            onChange={(e) => handleNewUserInputChange('firstName', e.target.value)}
          />
          <br />
          <label>Last Name: </label>
          <input
            type="text"
            value={newUser.lastName}
            onChange={(e) => handleNewUserInputChange('lastName', e.target.value)}
          />
          <br />
          <label>Email: </label>
          <input
            type="text"
            value={newUser.email}
            onChange={(e) => handleNewUserInputChange('email', e.target.value)}
          />
          <br />
          <button onClick={handleSaveNewUser}>Save</button>
          <button onClick={handleCancelNewUser}>Cancel</button>
        </div>
      )}

    <div className='tablediv'>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>
                {editingUserId === user.id ? (
                  <input
                    type="text"
                    value={user.firstName}
                    onChange={(e) => handleInputChange(user.id, 'firstName', e.target.value)}
                  />
                ) : (
                  user.firstName
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <input
                    type="text"
                    value={user.lastName}
                    onChange={(e) => handleInputChange(user.id, 'lastName', e.target.value)}
                  />
                ) : (
                  user.lastName
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <input
                    type="text"
                    value={user.email}
                    onChange={(e) => handleInputChange(user.id, 'email', e.target.value)}
                  />
                ) : (
                  user.email
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <>
                    <button onClick={() => handleSave(user.id)}>Save</button>
                    <button onClick={handleCancelEdit}>Cancel</button>
                  </>
                ) : (
                    <div>
                    <AiFillEdit color='blue' size={18} onClick={() => handleEdit(user.id)}/>
                    </div>
                )}
                <div>
                <AiFillDelete className='del' color='red' size={18} onClick={() => handleDelete(user.id)} />
                </div>

              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
 </div>
  );
};

export default UserManagementPage;
