import { useContext } from 'react';
import { CDSDropdownContext } from '../context.esm.js';

const usePlacement = isNested => {
  const {
    placement
  } = useContext(CDSDropdownContext);
  if (isNested) {
    return placement === 'left' ? 'right-start' : 'left-start';
  }
  return placement === 'left' ? 'bottom-start' : 'bottom-end';
};

export { usePlacement };
