import { IcInfo } from '../svgs/ic-info.esm.js';
import { IcDisabled } from '../svgs/ic-disabled.esm.js';
import { IcInactive } from '../svgs/ic-inactive.esm.js';
import { IcActive } from '../svgs/ic-active.esm.js';
import { IcWarning } from '../svgs/ic-warning.esm.js';
import { IcPositive } from '../svgs/ic-positive.esm.js';
import { IcNegative } from '../svgs/ic-negative.esm.js';

const handleStatus = status => {
  switch (status) {
    case 'negative':
      return IcNegative;
    case 'positive':
      return IcPositive;
    case 'warning':
      return IcWarning;
    case 'active':
      return IcActive;
    case 'inactive':
      return IcInactive;
    case 'disabled':
      return IcDisabled;
    case 'info':
    default:
      return IcInfo;
  }
};

export { handleStatus };
