/**
 * Sentiment theming tokens are organized as:
 * [sentiment-][usage][-prominence][-interact]
 * where "usage" is the only _required_ token.
 *
 * @see https://youtu.be/ylDed18OVdY?t=617
 */
export declare const SentimentProminenceArray: readonly ["", "weak", "medium", "strong"];
export type TSentimentProminence = (typeof SentimentProminenceArray)[number];
export declare const BrandSentimentArray: readonly ["brand", "brand-accent", "inverse", "base"];
export type TBrandSentiment = (typeof BrandSentimentArray)[number];
export declare const StatusSentimentArray: readonly ["positive", "info", "warning", "severe-warning", "negative", "dormant"];
export type TStatusSentiment = (typeof StatusSentimentArray)[number];
export declare const InteractionSentimentArray: readonly ["control", "interact"];
export type TInteractionSentiment = (typeof InteractionSentimentArray)[number];
export declare const CategoricalSentimentArray: readonly ["accent-a", "accent-b", "accent-c", "accent-d", "accent-e", "accent-f", "accent-g", "accent-h", "accent-i", "accent-j", "accent-k"];
export type TCategoricalSentiment = (typeof CategoricalSentimentArray)[number];
export declare const SentimentArray: readonly ["brand", "brand-accent", "inverse", "base", "positive", "info", "warning", "severe-warning", "negative", "dormant", "control", "interact"];
export type TSentiment = (typeof SentimentArray)[number];
export declare const AllSentimentArray: readonly ["brand", "brand-accent", "inverse", "base", "positive", "info", "warning", "severe-warning", "negative", "dormant", "control", "interact", "accent-a", "accent-b", "accent-c", "accent-d", "accent-e", "accent-f", "accent-g", "accent-h", "accent-i", "accent-j", "accent-k"];
export type TAllSentiment = (typeof AllSentimentArray)[number];
export declare const SentimentGradientArray: readonly ["inverse", "brand"];
export type TSentimentGradient = (typeof SentimentGradientArray)[number];
export declare const SentimentUsageArray: readonly ["bg", "bg-gradient", "text", "text-in", "icon", "icon-in", "border", "border-in"];
export type TSentimentUsage = (typeof SentimentUsageArray)[number];
export declare const SentimentInteractArray: readonly ["default", "hover", "focus", "active", "disabled"];
export type TSentimentInteractUsage = (typeof SentimentInteractArray)[number];
export type CDSThemeObj = {
    sentiment?: TSentiment;
    usage?: TSentimentUsage;
    prominence?: TSentimentProminence;
};
