import { type Datum } from '@nivo/line';
import type { CDSChartAxis, CDSChartGrid } from './';
export interface CDSThemeMargins {
    bottom: number;
    left: number;
    right: number;
    top: number;
}
export interface CDSLine {
    line?: string;
    stroke?: string;
    strokeDasharray?: string;
    strokeWidth?: number;
}
export interface CDSCrosshair {
    line: CDSLine;
}
export interface CDSChartTheme {
    axis: CDSChartAxis;
    background: string;
    colors: (datum: Datum, i?: number) => string;
    crosshair: CDSCrosshair;
    fontFamily: string;
    fontSize: number;
    grid: CDSChartGrid;
    margin: CDSThemeMargins;
    pointSize: number;
    textColor: string;
}
