const MIN_TRUNCATION_DEFAULT = 5;
const truncateText = (children, truncated) => {
  const isTruncated = !!truncated;
  const truncationLength = typeof truncated === 'number' ? truncated : MIN_TRUNCATION_DEFAULT;
  const truncatedChildren = isTruncated && typeof children === 'string' && children.length > truncationLength ? children.slice(0, truncationLength) + '...' : children;
  return truncatedChildren;
};

export { MIN_TRUNCATION_DEFAULT, truncateText };
