import { useState } from 'react';

const useColumnOrder = ({
  columnPinning,
  columns
}) => {
  const [columnOrder, setColumnOrder] = useState([...(columnPinning.left ?? []), ...columns.flatMap(column => column.id && !columnPinning.left?.includes(column.id) && !columnPinning.right?.includes(column.id) ? column.id : []), ...(columnPinning.right ?? [])]);
  return {
    columnOrder,
    setColumnOrder
  };
};

export { useColumnOrder };
