import { cx } from '@ciscodesignsystems/cds-component-utilities';

const getPinnedStatus = column => {
  const isPinned = column.getIsPinned();
  const isLastLeftPinnedColumn = isPinned === 'left' && column.getIsLastColumn('left');
  const isFirstRightPinnedColumn = isPinned === 'right' && column.getIsFirstColumn('right');
  const pinnedClasses = cx({}, {
    'cds-table__column--pinned': isPinned,
    [`cds-table__column--pinned--${isPinned}`]: isPinned,
    [`cds-table__column--pinned--${isPinned}--with-shadow`]: isLastLeftPinnedColumn || isFirstRightPinnedColumn
  });
  return {
    isPinned,
    isLastLeftPinnedColumn,
    isFirstRightPinnedColumn,
    pinnedClasses
  };
};

export { getPinnedStatus };
