import { useState } from 'react';

const useColumnPinning = ({
  enablePinning,
  columnPinning: columnPinningFromProps,
  enableReorderRows,
  expandable,
  selectable,
  enableColumnsDisplaySettings
}) => {
  const initialColumnPinningState = {
    left: [...(enableReorderRows ? ['row-sort-dnd'] : []), ...(expandable ? ['expand'] : []), ...(selectable ? ['select'] : []), ...(columnPinningFromProps?.left ?? [])],
    right: [...(columnPinningFromProps?.right ?? []), ...(enableColumnsDisplaySettings ? ['settings'] : [])]
  };
  const [columnPinning, setColumnPinning] = useState(enablePinning ? initialColumnPinningState : {});
  return {
    enablePinning,
    columnPinning,
    setColumnPinning
  };
};

export { useColumnPinning };
